INSERT INTO "WellnessPass" (id, slug, title, description, "totalDays", "isActive", "rewardDiscountPercent", "rewardValidDays", "rewardText", "createdAt", "updatedAt")
INSERT INTO "WellnessPassDay" (id, "passId", "dayNumber", title, content) VALUES
  ('d_zb_01', 'pass_zhivoy_balans', 1, 'Знакомство с кожей', 'Оцените текущее состояние кожи. Сделайте фото без макияжа при дневном свете. Запишите, что вас беспокоит.'),
  ('d_zb_02', 'pass_zhivoy_balans', 2, 'Очищение', 'Утром и вечером — мягкое очищение. Откажитесь от мыла, используйте гель или пенку по типу кожи.'),
  ('d_zb_03', 'pass_zhivoy_balans', 3, 'Водный баланс', 'Выпейте не менее 8 стаканов воды. Увлажните кожу кремом после каждого умывания.'),
  ('d_zb_04', 'pass_zhivoy_balans', 4, 'Питание кожи', 'Нанесите питательную маску на 15 минут. Подойдёт маска с гиалуроновой кислотой или алоэ.'),
  ('d_zb_05', 'pass_zhivoy_balans', 5, 'Защита от солнца', 'Начните использовать SPF ежедневно, даже в пасмурную погоду. Это основа здоровой кожи.'),
  ('d_zb_06', 'pass_zhivoy_balans', 6, 'Массаж лица', 'Сделайте лёгкий массаж лица чистыми руками по массажным линиям — 5 минут утром.'),
  ('d_zb_07', 'pass_zhivoy_balans', 7, 'День отдыха кожи', 'Минимум средств на лице. Только мягкое очищение и увлажняющий крем. Дайте коже подышать.'),
  ('d_zb_08', 'pass_zhivoy_balans', 8, 'Тоник и тонер', 'Добавьте тоник в уход — после очищения, перед кремом. Он выравнивает pH и готовит кожу.'),
  ('d_zb_09', 'pass_zhivoy_balans', 9, 'Зона вокруг глаз', 'Нанесите крем для кожи вокруг глаз. Лёгкие похлопывания безымянным пальцем.'),
  ('d_zb_10', 'pass_zhivoy_balans', 10, 'Отшелушивание', 'Используйте мягкий пилинг или скатку. Не чаще раза в неделю. Кожа станет гладкой и сияющей.'),
  ('d_zb_11', 'pass_zhivoy_balans', 11, 'Глубокое увлажнение', 'Нанесите сыворотку с гиалуроновой кислотой под крем. Сыворотка проникает глубже обычного крема.'),
  ('d_zb_12', 'pass_zhivoy_balans', 12, 'Питание изнутри', 'Включите в рацион продукты с Омега-3: рыбу, орехи, авокадо. Кожа скажет спасибо.'),
  ('d_zb_13', 'pass_zhivoy_balans', 13, 'Паровая ванночка', 'Распарьте лицо над тёплым отваром ромашки 5-7 минут. Это раскроет поры и усилит действие масок.'),
  ('d_zb_14', 'pass_zhivoy_balans', 14, 'Середина пути', 'Сделайте фото и сравните с первым днём. Запишите изменения. Вы на полпути!'),
  ('d_zb_15', 'pass_zhivoy_balans', 15, 'Ночной уход', 'Начните использовать ночной крем. Ночью кожа восстанавливается активнее — помогите ей.'),
  ('d_zb_16', 'pass_zhivoy_balans', 16, 'Гимнастика для лица', 'Сделайте 5 упражнений для мышц лица. Это улучшает тонус и овал.'),
  ('d_zb_17', 'pass_zhivoy_balans', 17, 'Холодный уход', 'Протрите лицо кубиком льда из отвара зелёного чая. Это тонизирует и сужает поры.'),
  ('d_zb_18', 'pass_zhivoy_balans', 18, 'Маска-пленка', 'Нанесите увлажняющую маску-плёнку. Она мягко удалит загрязнения и подтянет кожу.'),
  ('d_zb_19', 'pass_zhivoy_balans', 19, 'Уход за шеей', 'Не забывайте про шею и декольте — они тоже нуждаются в увлажнении и защите SPF.'),
  ('d_zb_20', 'pass_zhivoy_balans', 20, 'Антиоксиданты', 'Добавьте сыворотку с витамином C. Она защищает от свободных радикалов и выравнивает тон.'),
  ('d_zb_21', 'pass_zhivoy_balans', 21, 'Детокс', 'Лёгкий день: только чистая вода, зелёный чай, свежие овощи. Минимум сахара и кофе.'),
  ('d_zb_22', 'pass_zhivoy_balans', 22, 'Глиняная маска', 'Нанесите маску из белой или голубой глины на 10 минут. Отлично очищает и матирует.'),
  ('d_zb_23', 'pass_zhivoy_balans', 23, 'Масло для лица', 'Попробуйте масло жожоба или сквалан перед сном. 2-3 капли — достаточно.'),
  ('d_zb_24', 'pass_zhivoy_balans', 24, 'Сон красоты', 'Спите на шёлковой наволочке (или атласной). Это уменьшает трение и заломы на коже.'),
  ('d_zb_25', 'pass_zhivoy_balans', 25, 'Патчи для глаз', 'Используйте гидрогелевые патчи на 15 минут. Они уберут отёчность и освежат взгляд.'),
  ('d_zb_26', 'pass_zhivoy_balans', 26, 'Финальная маска', 'Нанесите вашу любимую маску на 20 минут. Расслабьтесь и наслаждайтесь процессом.'),
  ('d_zb_27', 'pass_zhivoy_balans', 27, 'Закрепление привычек', 'Запишите свой идеальный утренний и вечерний ритуал ухода. Сделайте это привычкой.'),
  ('d_zb_28', 'pass_zhivoy_balans', 28, 'Итоги и награда', 'Поздравляем! Сделайте финальное фото, сравните с первым днём. Заберите вашу награду!');
INSERT INTO "WellnessPass" (id, slug, title, description, "totalDays", "isActive", "rewardDiscountPercent", "rewardValidDays", "rewardText", "createdAt", "updatedAt")
INSERT INTO "WellnessPassDay" (id, "passId", "dayNumber", title, content) VALUES
  ('d_si_01', 'pass_siyanie_iznutri', 1, 'Начало пути', 'Оцените тон кожи и запишите проблемные зоны. Цель программы — ровный сияющий тон.'),
  ('d_si_02', 'pass_siyanie_iznutri', 2, 'Двойное очищение', 'Вечером — масло для снятия макияжа, затем пенка. Двойное очищение — основа чистой кожи.'),
  ('d_si_03', 'pass_siyanie_iznutri', 3, 'Кислотный тоник', 'Начните использовать тоник с AHA-кислотами (гликолевая, молочная). Мягко отшелушивает.'),
  ('d_si_04', 'pass_siyanie_iznutri', 4, 'Витамин C', 'Нанесите сыворотку с витамином C утром под SPF. Это осветлит пигментацию и добавит сияния.'),
  ('d_si_05', 'pass_siyanie_iznutri', 5, 'Увлажнение', 'Базовый шаг: тонер → сыворотка → крем. Увлажнённая кожа отражает свет лучше.'),
  ('d_si_06', 'pass_siyanie_iznutri', 6, 'Зелёный чай', 'Замените одну чашку кофе на зелёный чай. Антиоксиданты работают и изнутри.'),
  ('d_si_07', 'pass_siyanie_iznutri', 7, 'Выходной для кожи', 'Без макияжа весь день. Только уход. Позвольте коже сиять самостоятельно.'),
  ('d_si_08', 'pass_siyanie_iznutri', 8, 'Энзимный пилинг', 'Используйте энзимный пилинг вместо скраба. Он мягче и подходит чувствительной коже.'),
  ('d_si_09', 'pass_siyanie_iznutri', 9, 'Ниацинамид', 'Добавьте средство с ниацинамидом — он выравнивает тон и сужает поры.'),
  ('d_si_10', 'pass_siyanie_iznutri', 10, 'Ретинол (мягко)', 'Вечером — средство с низким % ретинола. Начинайте через день, следите за реакцией.'),
  ('d_si_11', 'pass_siyanie_iznutri', 11, 'Маска для сияния', 'Нанесите маску с витамином C или AHA на 10-15 минут. Мгновенный эффект свечения.'),
  ('d_si_12', 'pass_siyanie_iznutri', 12, 'Сон и кожа', 'Обеспечьте 7-8 часов сна. Мелатонин — лучший антиоксидант для восстановления кожи.'),
  ('d_si_13', 'pass_siyanie_iznutri', 13, 'Тканевая маска', 'Используйте тканевую маску с улиточным муцином или центеллой — восстановление и сияние.'),
  ('d_si_14', 'pass_siyanie_iznutri', 14, 'Проверка прогресса', 'Половина пути! Сфотографируйте лицо и сравните с днём 1. Отметьте улучшения.'),
  ('d_si_15', 'pass_siyanie_iznutri', 15, 'Лимфодренаж', 'Лёгкий массаж гуаша или руками — от центра к периферии. 5 минут утром снимут отёки.'),
  ('d_si_16', 'pass_siyanie_iznutri', 16, 'Арбутин', 'Средство с арбутином поможет осветлить пигментные пятна. Наносите точечно.'),
  ('d_si_17', 'pass_siyanie_iznutri', 17, 'Защита барьера', 'Используйте крем с церамидами. Здоровый барьер = ровный тон и меньше воспалений.'),
  ('d_si_18', 'pass_siyanie_iznutri', 18, 'Детокс-день', 'Исключите сахар и молочные продукты на день. Наблюдайте за кожей завтра.'),
  ('d_si_19', 'pass_siyanie_iznutri', 19, 'Мист для лица', 'Держите термальный мист под рукой. Освежайте кожу в течение дня — это поддерживает сияние.'),
  ('d_si_20', 'pass_siyanie_iznutri', 20, 'Пептиды', 'Сыворотка с пептидами вечером — стимулирует коллаген и улучшает текстуру кожи.'),
  ('d_si_21', 'pass_siyanie_iznutri', 21, 'Азелаиновая кислота', 'Нанесите крем с азелаиновой кислотой — она борется с покраснениями и пигментацией.'),
  ('d_si_22', 'pass_siyanie_iznutri', 22, 'Альгинатная маска', 'Попробуйте альгинатную маску. Она моделирует овал и моментально улучшает тон.'),
  ('d_si_23', 'pass_siyanie_iznutri', 23, 'Физическая активность', 'Тренировка или быстрая прогулка 30 минут — улучшает кровообращение и цвет лица.'),
  ('d_si_24', 'pass_siyanie_iznutri', 24, 'Масло шиповника', 'Пара капель масла шиповника перед сном — витамины А и С для восстановления кожи.'),
  ('d_si_25', 'pass_siyanie_iznutri', 25, 'Криомассаж', 'Протрите лицо ледяной ложкой или крио-роликом — мгновенный тонус и сияние.'),
  ('d_si_26', 'pass_siyanie_iznutri', 26, 'Финальный уход', 'Проведите полноценную процедуру: очищение → пилинг → маска → сыворотка → крем.'),
  ('d_si_27', 'pass_siyanie_iznutri', 27, 'Закрепление', 'Составьте свой идеальный уход на основе опыта программы. Что работает лучше всего?'),
  ('d_si_28', 'pass_siyanie_iznutri', 28, 'Итоги и награда', 'Финал! Сделайте фото — вы сияете! Заберите скидку на профессиональную чистку лица.');
INSERT INTO "WellnessPass" (id, slug, title, description, "totalDays", "isActive", "rewardDiscountPercent", "rewardValidDays", "rewardText", "createdAt", "updatedAt")
INSERT INTO "WellnessPassDay" (id, "passId", "dayNumber", title, content) VALUES
  ('d_ud_01', 'pass_ulybka_dushi', 1, 'Точка отсчёта', 'Опишите своё состояние прямо сейчас. Что чувствуете? Что хотите изменить за 28 дней?'),
  ('d_ud_02', 'pass_ulybka_dushi', 2, 'Осознанное дыхание', 'Утром — 5 минут глубокого дыхания: вдох на 4, задержка на 4, выдох на 6.'),
  ('d_ud_03', 'pass_ulybka_dushi', 3, 'Благодарность', 'Запишите 3 вещи, за которые вы благодарны сегодня. Маленькие или большие — неважно.'),
  ('d_ud_04', 'pass_ulybka_dushi', 4, 'Цифровой детокс', 'На 2 часа вечером — без телефона. Почитайте, погуляйте, просто побудьте с собой.'),
  ('d_ud_05', 'pass_ulybka_dushi', 5, 'Прогулка на природе', 'Погуляйте 20-30 минут в парке или лесу. Обратите внимание на звуки и запахи.'),
  ('d_ud_06', 'pass_ulybka_dushi', 6, 'Тёплая ванна', 'Примите ванну с морской солью или эфирным маслом лаванды. Расслабьте тело.'),
  ('d_ud_07', 'pass_ulybka_dushi', 7, 'День без спешки', 'Делайте всё осознанно и медленно. Ешьте медленно. Идите медленно. Замечайте мир.'),
  ('d_ud_08', 'pass_ulybka_dushi', 8, 'Медитация', 'Попробуйте 10-минутную медитацию. Сядьте удобно, закройте глаза, наблюдайте за дыханием.'),
  ('d_ud_09', 'pass_ulybka_dushi', 9, 'Письмо себе', 'Напишите письмо себе через год. Что вы хотите для себя? Каким хотите стать?'),
  ('d_ud_10', 'pass_ulybka_dushi', 10, 'Музыкотерапия', 'Создайте плейлист из 10 песен, которые поднимают настроение. Слушайте его утром.'),
  ('d_ud_11', 'pass_ulybka_dushi', 11, 'Растяжка', '15 минут мягкой растяжки или йоги. Расслабьте шею, плечи, спину.'),
  ('d_ud_12', 'pass_ulybka_dushi', 12, 'Творчество', 'Порисуйте, напишите стихотворение или приготовьте новое блюдо. Любое творчество.'),
  ('d_ud_13', 'pass_ulybka_dushi', 13, 'Добрый поступок', 'Сделайте что-то приятное для другого — без повода. Комплимент, помощь, подарок.'),
  ('d_ud_14', 'pass_ulybka_dushi', 14, 'Середина пути', 'Запишите, как изменилось ваше состояние. Что нового вы заметили? Что помогает?'),
  ('d_ud_15', 'pass_ulybka_dushi', 15, 'Тишина', 'Проведите 30 минут в полной тишине. Без музыки, телефона, разговоров.'),
  ('d_ud_16', 'pass_ulybka_dushi', 16, 'Массаж', 'Сделайте себе массаж стоп или рук. Или запишитесь на профессиональный массаж.'),
  ('d_ud_17', 'pass_ulybka_dushi', 17, 'Ароматерапия', 'Зажгите свечу с приятным ароматом или используйте диффузор с эфирными маслами.'),
  ('d_ud_18', 'pass_ulybka_dushi', 18, 'Границы', 'Скажите «нет» одной вещи, которая отнимает энергию. Установите границу мягко, но твёрдо.'),
  ('d_ud_19', 'pass_ulybka_dushi', 19, 'Тёплое общение', 'Позвоните или встретьтесь с близким человеком. Настоящее общение — лучшее лекарство.'),
  ('d_ud_20', 'pass_ulybka_dushi', 20, 'Утренние страницы', 'Сразу после пробуждения напишите 3 страницы от руки — всё, что приходит в голову.'),
  ('d_ud_21', 'pass_ulybka_dushi', 21, 'Танцы', 'Включите любимую музыку и танцуйте 10 минут. Тело и настроение скажут спасибо.'),
  ('d_ud_22', 'pass_ulybka_dushi', 22, 'Лёгкий день', 'Разрешите себе ничего не делать. Отдых — это не лень, а забота о себе.'),
  ('d_ud_23', 'pass_ulybka_dushi', 23, 'Прощение', 'Подумайте, кого или за что вы хотите простить. Прощение освобождает вас, не других.'),
  ('d_ud_24', 'pass_ulybka_dushi', 24, 'Визуализация', 'Закройте глаза и представьте своё идеальное утро через год. В деталях. С эмоциями.'),
  ('d_ud_25', 'pass_ulybka_dushi', 25, 'Здоровый сон', 'Ложитесь в 22:00. Проветрите комнату. Без экранов за час до сна.'),
  ('d_ud_26', 'pass_ulybka_dushi', 26, 'Сканирование тела', 'Лёжа расслабленно, мысленно пройдите от макушки до пальцев ног. Где напряжение? Отпустите.'),
  ('d_ud_27', 'pass_ulybka_dushi', 27, 'Ритуал благодарности', 'Перед сном — 5 минут тишины и благодарности за прожитый день и пройденный путь.'),
  ('d_ud_28', 'pass_ulybka_dushi', 28, 'Итоги и награда', 'Вы прошли путь! Перечитайте письмо из дня 9. Заберите скидку на консультацию.');
INSERT INTO "WellnessPass" (id, slug, title, description, "totalDays", "isActive", "rewardDiscountPercent", "rewardValidDays", "rewardText", "createdAt", "updatedAt")
INSERT INTO "WellnessPassDay" (id, "passId", "dayNumber", title, content) VALUES
  ('d_ts_01', 'pass_tihaya_sila', 1, 'Первый шаг', 'Оцените общее состояние: энергия, кожа, настроение. Запишите по шкале 1-10.'),
  ('d_ts_02', 'pass_tihaya_sila', 2, 'Стакан воды натощак', 'Начните день со стакана тёплой воды с лимоном. Запускает метаболизм и увлажняет кожу.'),
  ('d_ts_03', 'pass_tihaya_sila', 3, 'Контрастный душ', 'Завершите душ 30 секундами прохладной воды. Это тонизирует кожу и укрепляет сосуды.'),
  ('d_ts_04', 'pass_tihaya_sila', 4, 'Антиоксидантный завтрак', 'Ягоды, орехи, зелёный чай — идеальное начало дня для кожи и здоровья.'),
  ('d_ts_05', 'pass_tihaya_sila', 5, 'Мягкий скраб', 'Используйте мягкий скраб для тела — круговыми движениями от ног к сердцу.'),
  ('d_ts_06', 'pass_tihaya_sila', 6, 'Масло для тела', 'После душа — масло кокоса или миндаля на влажную кожу. Мягкость на весь день.'),
  ('d_ts_07', 'pass_tihaya_sila', 7, 'Отдых', 'День восстановления. Побалуйте себя: тёплый чай, хорошая книга, ранний сон.'),
  ('d_ts_08', 'pass_tihaya_sila', 8, 'Коллаген изнутри', 'Включите в рацион костный бульон или добавку коллагена. Кожа станет более упругой.'),
  ('d_ts_09', 'pass_tihaya_sila', 9, 'Утренняя зарядка', '10 минут лёгких упражнений утром — разгоняем кровь и тонизируем мышцы.'),
  ('d_ts_10', 'pass_tihaya_sila', 10, 'Масло для волос', 'Нанесите репейное или аргановое масло на кончики волос на ночь. Сила начинается с деталей.'),
  ('d_ts_11', 'pass_tihaya_sila', 11, 'Правильная осанка', 'Следите за осанкой весь день. Прямая спина = уверенность + здоровое кровообращение.'),
  ('d_ts_12', 'pass_tihaya_sila', 12, 'Зелёный смузи', 'Шпинат + банан + миндальное молоко. Витамины и энергия в одном стакане.'),
  ('d_ts_13', 'pass_tihaya_sila', 13, 'Сухой массаж', 'Массаж тела сухой щёткой перед душем. Отшелушивает и стимулирует лимфоток.'),
  ('d_ts_14', 'pass_tihaya_sila', 14, 'Чекпоинт', 'Середина программы. Оцените энергию и кожу снова по шкале 1-10. Видите разницу?'),
  ('d_ts_15', 'pass_tihaya_sila', 15, 'Пилинг для ног', 'Позаботьтесь о стопах — ванночка + пемза + питательный крем. Мелочи важны.'),
  ('d_ts_16', 'pass_tihaya_sila', 16, 'Маска для волос', 'Питательная маска для волос на 20 минут. Волосы — часть общего впечатления.'),
  ('d_ts_17', 'pass_tihaya_sila', 17, 'Прогулка', '30-минутная прогулка на свежем воздухе. Свежий воздух — тоник для кожи и души.'),
